import { FormProvider, useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import toast from 'react-hot-toast';
import { ArrowPathIcon } from '@heroicons/react/24/outline';
import { Fragment } from 'react';

import { addProductSchema, AddProductFormData } from './schema';
import ModalShell from './ModalShell';
import BasicInfoSection from './sections/BasicInfoSection';
import MediaSection from './sections/MediaSection';
import PricingSection from './sections/PricingSection';
import InventorySection from './sections/InventorySection';
import ShippingSection from './sections/ShippingSection';

import { api } from '../../../api';
import { useAuth } from '../../../hooks/useAuth';

type Props = {
  open: boolean;
  onClose(): void;
  onCreated(): void;
};

export default function AddProductModal({ open, onClose, onCreated }: Props) {
  const { token } = useAuth();

  const methods = useForm<AddProductFormData>({
    resolver: zodResolver(addProductSchema),
    defaultValues: {
      categories: [],
      images: [],
      condition: 'new',
      hashtags: [],
      stock: 1,
      shipping_type: 'free'
    }
  });

async function onSubmit(data: AddProductFormData) {
  if (!token) {
    toast.error('Debes haber iniciado sesión');
    return;
  }

  try {
    /* 1. Subir imágenes */
    let image_urls: string[] = [];
    if (data.images.length) {
      const uploads = await Promise.all(
        data.images.map(async img => {
          const fd = new FormData();
          fd.append('file', img);
          const r = await api.post<{ url: string }>('/upload/', fd, {
            headers: { 'Content-Type': 'multipart/form-data' }
          });
          return r.data.url;
        })
      );
      image_urls = uploads;
    }

    /* 2. Construir payload coherente con la API */
    const payload = {
      name: data.name,
      description: data.description,
      price_per_h: data.price,           // 👈 se mapea
      compare_at_price: data.compare_at_price || undefined,
      categories: data.categories.length ? data.categories : undefined,
      image_urls,
      sku: data.sku || undefined,
      stock: data.stock,
      weight_kg: data.weight_kg,
      shipping_type: data.shipping_type,
      condition: data.condition,
      hashtags: data.hashtags.filter(Boolean)     // quita strings vacíos
    };

    await api.post('/items/', payload);

    toast.success('¡Producto publicado!');
    methods.reset();
    onCreated();
    onClose();
  } catch (err: any) {
    console.error(err);
    toast.error(err.response?.data?.detail ?? 'Error al crear producto');
  }
}

  return (
    <ModalShell
      open={open}
      title="Nuevo producto"
      onClose={() => {
        methods.reset();
        onClose();
      }}
    >
      <FormProvider {...methods}>
        <form
          onSubmit={methods.handleSubmit(onSubmit)}
          className="grid grid-cols-1 sm:grid-cols-2 gap-8 overflow-y-auto px-6 py-8"
        >
          {/* Columna izquierda */}
          <div className="space-y-10">
            <BasicInfoSection />
            <MediaSection />
          </div>

          {/* Columna derecha */}
          <div className="space-y-10">
            <PricingSection />
            <InventorySection />
            <ShippingSection />
          </div>

          {/* Footer */}
          <div className="sm:col-span-2 flex justify-end gap-4 mt-6">
            <button
              type="button"
              className="btn--ghost px-6 py-3 rounded-lg text-sm font-medium transition-all hover:shadow-md dark:hover:bg-gray-700"
              onClick={() => {
                methods.reset();
                onClose();
              }}
            >
              Cancelar
            </button>
            <button
              className="btn px-6 py-3 rounded-lg text-sm font-medium flex items-center justify-center"
              disabled={methods.formState.isSubmitting}
            >
              {methods.formState.isSubmitting ? (
                <>
                  <ArrowPathIcon className="mr-2 h-5 w-5 animate-spin" />
                  Publicando…
                </>
              ) : (
                'Publicar'
              )}
            </button>
          </div>
        </form>
      </FormProvider>
    </ModalShell>
  );
}

import { z } from 'zod';

const MB = 1024 * 1024;
export const MAX_IMAGES = 10;

export const priceRegex = /^\d+([.,]\d{1,2})?$/;

export const addProductSchema = z.object({
  // Información básica
  name: z.string().min(3, 'Mínimo 3 caracteres'),
  description: z.string().max(500).optional(),
  categories: z.array(z.number()).min(1, 'Selecciona al menos una categoría'),
  condition: z.enum(['new', 'used']),
  hashtags: z.array(z.string()).max(5, 'Máx. 5 hashtags'),

  // Media
  images: z
    .array(
      z
        .instanceof(File)
        .refine(f => f.size <= 5 * MB, 'Cada imagen máx. 5 MB')
    )
    .min(1, 'Al menos 1 imagen')
    .max(MAX_IMAGES, `Máx. ${MAX_IMAGES} imágenes`),

  // Pricing
  price: z
    .string()
    .regex(priceRegex, 'Precio inválido')
    .transform(v => Number(v.replace(',', '.'))),
  compare_at_price: z
    .string()
    .regex(priceRegex, 'Precio inválido')
    .transform(v => (v ? Number(v.replace(',', '.')) : undefined))
    .optional(),

  // Inventory
  sku: z.string().max(40).optional(),
  stock: z.number().int().min(0).default(1),

  // Shipping
  weight_kg: z
    .string()
    .regex(priceRegex, 'Número inválido')
    .transform(v => Number(v.replace(',', '.'))),
  shipping_type: z.enum(['free', 'local_pickup', 'paid'])
});

export type AddProductFormData = z.infer<typeof addProductSchema>;

import { useEffect, useState } from 'react';

export default function useImagePreviews(files: File[]) {
  const [previews, setPreviews] = useState<string[]>([]);

  useEffect(() => {
    if (!files.length) {
      setPreviews([]);
      return;
    }
    const urls = files.map(f => URL.createObjectURL(f));
    setPreviews(urls);
    return () => urls.forEach(URL.revokeObjectURL);
  }, [files]);

  return previews;
}

