import { Link, NavLink } from 'react-router-dom';
import { Disclosure, Transition } from '@headlessui/react';
import {
  Bars3Icon,
  XMarkIcon,
  MoonIcon,
  SunIcon,
  PlusIcon,
} from '@heroicons/react/24/outline';
import logo from '../../assets/logo.png';
import { useAuth } from '../../hooks/useAuth';
import { useEffect, useState } from 'react';

/* ------------------------------ Tema (clase .dark) ------------------------- */
function ThemeToggle() {
  const [dark, setDark] = useState(
    () => document.documentElement.classList.contains('dark')
  );

  /* persiste en localStorage para siguientes visitas */
  useEffect(() => {
    const cls = document.documentElement.classList;
    dark ? cls.add('dark') : cls.remove('dark');
    localStorage.setItem('theme', dark ? 'dark' : 'light');
  }, [dark]);

  return (
    <button
      title="Cambiar tema"
      onClick={() => setDark(!dark)}
      className="rounded-md p-2 hover:bg-gray-100 dark:hover:bg-gray-700"
    >
      {dark ? <SunIcon className="h-5 w-5" /> : <MoonIcon className="h-5 w-5" />}
      <span className="sr-only">Cambiar tema</span>
    </button>
  );
}

/* -------------------------------------------------------------------------- */
/*                                 Header                                     */
/* -------------------------------------------------------------------------- */
export default function Header() {
  const { token, logout } = useAuth();

  const links = token
    ? [
        { to: '/dashboard', label: 'Dashboard' },
        { to: '/dashboard#add', label: 'Publicar', cta: true },
        { label: 'Salir', action: logout },
      ]
    : [
        { to: '/login', label: 'Entrar' },
        { to: '/register', label: 'Crear cuenta', cta: true },
      ];

  /* ---------- efecto de fondo blur‑>sólido al hacer scroll ---------- */
  const [scrolled, setScrolled] = useState(false);
  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 10);
    onScroll();
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  return (
    <Disclosure
      as="header"
      className={`sticky top-0 z-40 w-full transition-colors ${
        scrolled
          ? 'bg-white/90 shadow-sm dark:bg-gray-900/90'
          : 'bg-white/60 dark:bg-gray-900/60'
      } backdrop-blur supports-backdrop-blur:bg-white/40`}
    >
      {({ open }) => (
        <>
          {/* ----------------------------- Barra principal ----------------------------- */}
          <div className="container mx-auto flex items-center justify-between py-3 md:py-4">
            {/* Logo */}
            <Link to="/" aria-label="Inicio" className="flex items-center gap-2">
              <img
                src={logo}
                alt="Logo Rental‑MVP"
                className="h-10 w-auto select-none md:h-12"
                decoding="async"
              />
              <span className="sr-only">Rental‑MVP</span>
            </Link>

            {/* Navegación escritorio */}
            <nav className="hidden items-center gap-8 text-sm font-semibold md:flex">
              {links.map(({ to, label, action, cta }) =>
                action ? (
                  <button
                    key={label}
                    onClick={action}
                    className="transition-colors hover:text-brand dark:hover:text-brand"
                  >
                    {label}
                  </button>
                ) : (
                  <NavLink
                    key={label}
                    to={to!}
                    className={({ isActive }) =>
                      [
                        'transition-colors hover:text-brand',
                        isActive ? 'text-brand' : '',
                        cta
                          ? 'rounded-md bg-brand px-4 py-2 text-white shadow hover:bg-brand-hover dark:bg-brand-dark dark:hover:bg-brand-hover-dark'
                          : '',
                      ].join(' ')
                    }
                  >
                    {label}
                  </NavLink>
                )
              )}
              <ThemeToggle />
            </nav>

            {/* Botón hamburguesa móvil */}
            <div className="flex items-center gap-2 md:hidden">
              <ThemeToggle />
              <Disclosure.Button
                aria-label="Abrir menú"
                className="rounded-md p-2 hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                {open ? (
                  <XMarkIcon className="h-6 w-6" />
                ) : (
                  <Bars3Icon className="h-6 w-6" />
                )}
              </Disclosure.Button>
            </div>
          </div>

          {/* ----------------------------- Panel móvil ------------------------------ */}
          <Transition
            show={open}
            enter="transition duration-150 ease-out"
            enterFrom="opacity-0 -translate-y-2"
            enterTo="opacity-100 translate-y-0"
            leave="transition duration-100 ease-in"
            leaveFrom="opacity-100 translate-y-0"
            leaveTo="opacity-0 -translate-y-2"
          >
            <Disclosure.Panel className="border-t border-gray-200 bg-white/95 px-4 pb-6 dark:border-gray-700 dark:bg-gray-900/95 md:hidden">
              <nav className="flex flex-col gap-4 pt-6 text-base font-medium">
                {links.map(({ to, label, action, cta }) =>
                  action ? (
                    <button
                      key={label}
                      onClick={action}
                      className="text-left transition-colors hover:text-brand"
                    >
                      {label}
                    </button>
                  ) : (
                    <NavLink
                      key={label}
                      to={to!}
                      className={({ isActive }) =>
                        [
                          'transition-colors hover:text-brand',
                          isActive ? 'text-brand' : '',
                          cta
                            ? 'rounded-md bg-brand px-4 py-2 text-white shadow hover:bg-brand-hover dark:bg-brand-dark dark:hover:bg-brand-hover-dark'
                            : '',
                        ].join(' ')
